class Coupon {
  constructor(price, expiration){
    this.price = price;
    this.expiration = expiration || '2주';
  }
  getPriceText() {
    return `$ ${this.price}`;
  }
  getExpirationMessage(){
    return ` 이 쿠폰은 ${this.expiration} 후에 만료됩니다.`;
  }
  isRewardsEligible(user){
    return user.rewardsEligible && user.active;
  }
  getRewards(user){
    if (this.isRewardsEligible(user)) {
      this.price = this.price * 0.9;
    }
  }
}

export default Coupon;

// 새로운 인스턴스 생성
const coupon = new Coupon(); 
coupon.price;
// 5
coupon['expiration'];
// '2주'

const coupon2 = new Coupon(5);
coupon2.getPriceText();
// '$ 5'

import Coupon from './Coupon.js'
class FlashCoupon extends Coupon {
	constructor(price,expiration) {
    super(price);
    this.expiration = expiration || '2시간';
  }
  getExpirationMessage() {
    return `이 쿠폰은 깜짝 쿠폰이며 ${this.expiration} 후에 만료됩니다.`''
  }
  isRewardsEligible(user){
    return super.isRewardsEligible(user) && this.price > 20;
  }
  getRewards(user){
    if (this.isRewardsEligible(user)){
      this.price = this.price*0.8;
    }
  }
}
export {FlashCoupon};

const flash = new FlashCoupon(10);
flash.price;
// 10
flash.getPriceText();
// "$ 10"
flash.getExpirationMessage();
// "이 쿠폰은 2시간 후에 만료됩니다"

function Coupon(price, expiration) {
  this.prce = price;
  this.expiration = expiration || '2주';
}
const coupon = new Coupon(5,'2개월');
coupon.price;
// 5

Coupon.prototype.getExpirationMessage = function() {
  return `이 쿠폰은 ${this.expiration} 후에 만료됩니다.`;
};
coupon.getExpirationMessage();
// 이 쿠폰은 2개월 후에 만료됩니다.

class Coupon {
  constructor(price, expiration) {
    this.price = price;
    this.expiration = expiration || '2주';
  }
  get priceText() {
    return `${this.price}`;
  }
  get expirationMessage() {
    return `이 쿠폰은 ${this.expiration} 후에 만료됩니다.`
  }
  set halfPrice(price) {
    this.price = price /2;
  }
}

const coupon = new Coupon(5);
coupon.price = 10;
coupon.priceText;
// '$10'
coupon.messageText;
// '이 쿠폰은 2주 후에 만료됩니다.'

coupon.halfPrice = 20;
coupon.price;
// 10
coupon.halfPrice;
// undefined

class Coupon {
  constructor(price, expiration) {
    this._price = price;
    this.expiration = expiration || '2주';
  }
  get priceText() {
    return `$${this._price}`;
  }
  get price() {
    return this._price;
  }
  set price(price) {
    const newPrice = price;
    	.toString()
    	.replace(/[^\d]/g,'');
    this._price = parseInt(newPrice,10);
  }
  get expirationMessage() {
    return `이 쿠폰은 ${this.expiration} 후에 만료됩니다.`
  }
}
const coupon = new Coupon(5);
coupon.price;
// 5
coupon.price = '$10';
coupon.price;
// 10
coupon.priceText;
// $ 10

export default Coupon

function* getCairoTrilogy(){
  yield '궁전 샛길';
  yield '욕망의 궁전';
  yield '설탕 거리';
}

const trilogy = getCairoTrilogy();
trilogy.next();
// { value : '궁전 샛길', done : false }
trilogy.next();
// { value : '욕망의 궁전', done : false }
trilogy.next();
// { value : '설탕 거리', done : true }
trilogy.next();
// { value : undefined, done : true }

class FamilyTree {
  constructor(){
    this.family = {
      name:'Doris',
      child: {
        name:'Martha',
        child:{
          name:'Dyam',
          child:{
            name:'Bea',
          },
        },
      },
    };
  }
  *[Symbol.iterator]() {
    let node = this.family;
    while(node){
      yield node.name;
      node = node.child;
    }
  }
}

const family = new FamilyTree();
[...family];
// ['Doris','Martha','Dyan','Bea']

